# Méthode de rafraîchissement du tableau
def rafraichir_tableau(table_data)
  document = JS.global[:document]
  container = document.getElementById('table_container')

  # Vider complètement le conteneur
  container[:innerHTML] = ''

  # Création de la table
  table = document.createElement("table")
  table[:style][:width] = "100%"
  table[:id] = "mainTable"
  table[:className] = "my-csv-table"

  # Création et ajout de l'en-tête (première ligne) à la table
  if table_data.any?
    header_row = document.createElement("tr")
    table_data.first.keys.each do |header|
      th = document.createElement("th")
      th[:innerText] = header
      header_row.appendChild(th)
    end

    # Appliquer le style sticky à l'en-tête
    header_row[:style][:position] = "sticky"
    header_row[:style][:top] = "0"
    header_row[:style][:backgroundColor] = "#FFF" # Pour la visibilité
    header_row[:style][:zIndex] = "100"

    table.appendChild(header_row)
  end

  # Ajout des lignes de données à la table
  table_data.each do |row|
    tr = document.createElement("tr")
    row.each_with_index do |(key, value), index|
      # value = value.gsub("'", "\\'")
      td = document.createElement("td")
      # Appliquer la couleur rouge si la clé est 'Nom'
      if key == 'Nom'
        td[:style][:color] = 'red'
        td.addEventListener("mousedown") do |event|
          check_photo(value)
        end
        td[:innerHTML] = value
        td[:style][:width] = LARGEURS_COLONNES[index]
        tr.appendChild(td)
      elsif key == 'photo'
        value="medias/images/photos/#{value.gsub(" ","_")}.png"
        # value='medias/images/ballanim.png'
        img = document.createElement("img")
        img[:src] = value
        img[:style][:width] = "30px"
        img[:style][:height] = "30px"
        img.addEventListener("mousedown") do |event|
          take_photo(row)
        end

        img.addEventListener("error") do
          # Si l'image ne se charge pas, affichez une div rose
          div = document.createElement("div")
          div[:style][:width] = "30px"
          div[:style][:height] = "30px"
          div[:style][:backgroundColor] = "pink"
          div[:style][:display] = "flex"
          div[:style][:alignItems] = "center"
          div[:style][:justifyContent] = "center"
          div.addEventListener("mousedown") do |event|
            take_photo(row)
          end
          td.replaceChild(div, img)
        end

        td.appendChild(img)
        td[:style][:width] = LARGEURS_COLONNES[index]
        tr.appendChild(td)

      else
        td[:innerHTML] = value
        td[:style][:width] = LARGEURS_COLONNES[index]
        tr.appendChild(td)
      end

    end
    table.appendChild(tr)
  end

  # Ajouter la table au conteneur avec défilement
  scroll_container = document.createElement("div")
  scroll_container[:style][:overflowY] = "auto"
  scroll_container[:style][:maxHeight] = "300px"
  scroll_container.appendChild(table)
  container.appendChild(scroll_container)
end

# Méthode pour synchroniser la largeur des cellules
def synchroniser_largeur_cellules(header_table, body_table)
  header_cells = header_table.rows[0].cells
  body_cells = body_table.rows[0].cells

  header_cells.length.times do |i|
    width = body_cells[i].offsetWidth
    header_cells[i].style[:width] = "#{width}px"
  end
end

