def ajouter_colonne(nom_colonne, table_data)
  table_data.each { |row| row[nom_colonne] = "" }
  rafraichir_tableau(table_data)
end

def supprimer_colonne(nom_colonne, table_data)
  table_data.each { |row| row.delete(nom_colonne) }
  rafraichir_tableau(table_data)
end

def renommer_colonne(ancien_nom, nouveau_nom, table_data)
  table_data.each do |row|
    row[nouveau_nom] = row.delete(ancien_nom)
  end
  rafraichir_tableau(table_data)
end

def ajouter_rangs(nombre_de_rangs, table_data)
  # Vérifiez si table_data a des entrées pour obtenir la structure des clés
  if table_data.empty?
    puts "Impossible d'ajouter des rangs : table_data est vide."
    return
  end

  # Créez un rang vide basé sur les clés existantes
  rang_vide = table_data.first.each_with_object({}) { |(key, _), rang| rang[key] = "" }

  # Ajoutez le nombre spécifié de rangs vides
  nombre_de_rangs.times { table_data << rang_vide.clone }

  # Rafraîchir le tableau
  rafraichir_tableau(table_data)
end

def supprimer_rangs(indices, table_data)
  indices.sort.reverse_each { |index| table_data.delete_at(index) }
  rafraichir_tableau(table_data)
end

def remplir_cellule(rang, colonne, valeur, table_data)
  table_data[rang][colonne] = valeur
  rafraichir_tableau(table_data)
end

def trouver_indices_par_nom(table_data, chaine_recherchee)
  # Convertir la chaîne de recherche en minuscules pour une comparaison insensible à la casse
  chaine_recherchee_minuscules = chaine_recherchee.downcase

  # Trouver les indices
  indexes = table_data.each_with_index.map do |row, index|
    if row["Nom"].downcase.include?(chaine_recherchee_minuscules)
      index + 1 # Ajouter 1 car les indices commencent à 0
    end
  end.compact

  indexes
end

