def reveal(table_id, rangs_a_afficher)
  document = JS.global[:document]
  table = document.getElementById(table_id)
  return unless table

  lignes = table.getElementsByTagName('tr').to_a

  # Si rangs_a_afficher est vide, afficher toutes les lignes
  if rangs_a_afficher.empty?
    (1...lignes.length).each { |i| lignes[i][:style][:display] = '' }
  else
    # Sinon, afficher uniquement les lignes spécifiées
    (1...lignes.length).each do |i|
      lignes[i][:style][:display] = rangs_a_afficher.include?(i) ? '' : 'none'
    end
  end
end

