def dupliquer_colonne(nom_colonne_existante, nouveau_nom_colonne, table_data)
  # Vérifier si la colonne existante est présente
  if table_data.first.key?(nom_colonne_existante)
    table_data.each do |row|
      # Dupliquer les valeurs de l'ancienne colonne dans la nouvelle
      row[nouveau_nom_colonne] = row[nom_colonne_existante]
    end
  else
    puts "La colonne '#{nom_colonne_existante}' n'existe pas."
  end
  alert table_data
  rafraichir_tableau(table_data)
end

